INSERT INTO extreme_weather_facts (phenomenon, location, fact) VALUES
('Hurricane Katrina', 'New Orleans, USA', 'Hurricane Katrina was one of the deadliest hurricanes in U.S. history, causing over 1,800 deaths in 2005.'),
('The Dust Bowl', 'Great Plains, USA', 'The Dust Bowl of the 1930s was a severe drought and dust storm event that affected the U.S. and Canada, leading to crop failures and economic hardship.'),
('The Great Blizzard of 1888', 'Northeastern USA', 'The Great Blizzard of 1888 dumped up to 50 inches of snow in parts of the northeastern United States, causing over 400 deaths.'),
('Typhoon Haiyan', 'Philippines', 'Typhoon Haiyan, one of the strongest tropical cyclones ever recorded, hit the Philippines in 2013, causing over 6,000 fatalities.'),
('Mount St. Helens Eruption', 'Washington, USA', 'The 1980 eruption of Mount St. Helens was one of the most catastrophic volcanic events in U.S. history, with a significant loss of life and property.'),
('The Blizzard of 1978', 'Midwestern USA', 'The Blizzard of 1978 brought up to 3 feet of snow and winds of over 50 mph, leading to widespread closures and hundreds of fatalities.'),
('Cyclone Nargis', 'Myanmar', 'Cyclone Nargis, which hit Myanmar in 2008, killed over 138,000 people and caused extensive damage to the country''s infrastructure.'),
('The 1993 Storm of the Century', 'Eastern United States', 'The 1993 Storm of the Century was a massive snowstorm that affected the Eastern U.S. with heavy snow, ice, and high winds, causing over 300 deaths.'),
('Super Typhoon Tip', 'Western Pacific', 'Super Typhoon Tip, the largest and most intense tropical cyclone ever recorded, formed in 1979 and caused significant damage in Japan and the Philippines.'),
('El Nino', 'Pacific Ocean', 'El Nino is a climate phenomenon characterized by warmer-than-average sea surface temperatures in the Pacific Ocean, causing widespread weather disruptions.'),
('The 2003 European Heatwave', 'Europe', 'The 2003 European Heatwave caused over 70,000 heat-related deaths across several countries, including France, Germany, and Italy.'),
('The 2010 Pakistan Floods', 'Pakistan', 'The 2010 Pakistan Floods were among the worst floods in Pakistan''s history, displacing millions of people and causing over 1,700 deaths.'),
('The 1960 Tangshan Earthquake', 'China', 'The 1960 Tangshan Earthquake is the most deadly earthquake in Chinese history, with an estimated death toll of around 242,000.'),
('Hurricane Sandy', 'Caribbean and Eastern USA', 'Hurricane Sandy caused devastating storm surges, flooding, and widespread damage in 2012, particularly in New Jersey and New York.'),
('The 2011 Tohoku Earthquake', 'Japan', 'The 2011 Tohoku Earthquake, followed by a devastating tsunami, caused over 15,000 deaths in Japan and a nuclear disaster at Fukushima.'),
('The 2004 Indian Ocean Tsunami', 'Indian Ocean', 'The 2004 Indian Ocean Tsunami was triggered by an undersea earthquake and led to the deaths of over 230,000 people in 14 countries.'),
('Typhoon Yolanda', 'Philippines', 'Typhoon Yolanda (Haiyan) was one of the most powerful storms to make landfall in recorded history, causing catastrophic damage in the Philippines in 2013.'),
('The 1994 Northridge Earthquake', 'California, USA', 'The 1994 Northridge Earthquake caused significant damage in Los Angeles and surrounding areas, resulting in 57 deaths and widespread destruction.'),
('The 1953 North Sea Flood', 'UK/Netherlands', 'The 1953 North Sea Flood occurred after a storm surge and caused severe flooding in the UK and Netherlands, resulting in 1,836 deaths.'),
('Superstorm Sandy', 'USA', 'Superstorm Sandy, in 2012, caused widespread damage across the Caribbean, the Eastern USA, and parts of Canada, leading to 233 deaths.'),
('Mount Vesuvius Eruption', 'Italy', 'The eruption of Mount Vesuvius in 79 AD buried the Roman cities of Pompeii and Herculaneum under layers of ash, killing thousands.'),
('Tornado Outbreak of 2011', 'USA', 'The 2011 tornado outbreak, with more than 300 tornadoes across the U.S., was one of the deadliest, killing over 350 people.'),
('Cyclone Tracy', 'Australia', 'Cyclone Tracy devastated the city of Darwin, Australia, in 1974, killing 71 people and leaving most of the city''s buildings destroyed.'),
('The 2017 Hurricane Season', 'Atlantic Ocean', 'The 2017 Hurricane Season produced three major hurricanes: Harvey, Irma, and Maria, which devastated parts of Texas, Florida, and the Caribbean.'),
('The 2019 Cyclone Idai', 'Mozambique', 'Cyclone Idai hit Mozambique, Zimbabwe, and Malawi in 2019, causing over 1,000 deaths and displacing millions of people.');
